var tipuesearch = {"pages": [{'title': 'Dart 迴圈程式', 'text': 'W1 \n Gita: https://gist.github.com/40823147/1d4292cdc1748c8f89dc7e9e05fb49f2 \n Dartpad: https://mde.tw/dartpad/?id=1d4292cdc1748c8f89dc7e9e05fb49f2 \n 原始碼: \n void main() {\n    print("哈囉!");\n} \n \n W2 \n Gits: https://gist.github.com/40823147/33eae5a0423ed36e8fe679641932807d \n Dartpad： https://mde.tw/dartpad/?id=33eae5a0423ed36e8fe679641932807d \n 原始碼: \n \n int i;\nint sum;\n \nmain(){\n  sum = 0;\n  for(i=1;i <= 10 ;i++){\n    sum += i;\n    print("$sum");\n  }\n  print(\'sum = $sum\');\n}\n \n \n \n', 'tags': '', 'url': 'Dart 迴圈程式.html'}, {'title': 'Dart 亂數分組', 'text': 'Gits: https://gist.github.com/40823147/cf15f2e897874078c14b0d61b97e3d3c \n Dartpad: https://mde.tw/dartpad/?id=cf15f2e897874078c14b0d61b97e3d3c \n 原始碼: \n import   \'dart:html\' ; \n \xa0 \n void   main() { \n \xa0\xa0 // 每一組 10 人 \n \xa0\xa0 int num = 10; \n \xa0\xa0 // 組序由 1 開始 \n \xa0\xa0 int gth = 1; \n \xa0\xa0 // 迴圈序號變數 \n \xa0\xa0 int i; \n \xa0\xa0 // 每組學員暫存數列 \n \xa0\xa0 var gp_list = []; \n \xa0\xa0 // 全班分組數列 \n \xa0\xa0 var cp2019 = []; \n \xa0 \n \xa0\xa0 HttpRequest.getString( \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 \'https://mde.tw/cp2019/downloads/2019fall_cp_1a_list.txt\' ) \n \xa0\xa0\xa0\xa0\xa0\xa0 .then((String resp) { \n \xa0\xa0\xa0\xa0 // 利用 trim() 去除字串最後的跳行符號, 之後再利用 split() 根據 \\n 轉為數列 \n \xa0\xa0\xa0\xa0 var stud_list = resp.trim().split( "\\n" ); \n \xa0\xa0\xa0\xa0 // 數列利用 shuffle() 方法以隨機方法弄亂順序 \n \xa0\xa0\xa0\xa0 stud_list.shuffle(); \n \xa0\xa0\xa0\xa0 // 逐一讀取已經弄亂的學生學號數列, 利用模數運算每幾人分成一組 \n \xa0\xa0\xa0\xa0 for   (i = 0; i < stud_list.length; i++) { \n \xa0\xa0\xa0\xa0\xa0\xa0 // 0, 1~(num-1), num \n \xa0\xa0\xa0\xa0\xa0\xa0 if   (i % num == 0) { \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 gp_list = []; \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 // 列印區隔符號 \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 print( \'=\'   * 20); \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 print( "group $gth :" ); \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 print(stud_list[i]); \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 // 在各分組數列中加入將對應的學員學號 \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 gp_list.add(stud_list[i]); \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 gth = gth + 1; \n \xa0\xa0\xa0\xa0\xa0\xa0 }  else   { \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 print(stud_list[i]); \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 // 在各分組數列中加入將對應的學員學號 \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 gp_list.add(stud_list[i]); \n \xa0\xa0\xa0\xa0\xa0\xa0 } \n \xa0\xa0\xa0\xa0\xa0\xa0 if   (i % num == 0) { \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 // 逐步將各組暫存的分組數列加入全班分組數列中 \n \xa0\xa0\xa0\xa0\xa0\xa0\xa0\xa0 cp2019.add(gp_list); \n \xa0\xa0\xa0\xa0\xa0\xa0 } \n \xa0\xa0\xa0\xa0 } \n \xa0\xa0\xa0\xa0 // 列出全班分組數列 \n \xa0\xa0\xa0\xa0 print(cp2019); \n \xa0\xa0 }); \n } \n \n', 'tags': '', 'url': 'Dart 亂數分組.html'}]};